// Load the HTML data
const html = $json["data"]; // Replace "data" with your actual JSON field name

// Regular expressions to match the desired values
const dateRegex = /Date &amp; Time:<\/p>\s*<p>(.*?)<\/p>/;
const amountDebitedRegex = /Amount Debited:<\/p>\s*<p>(.*?)<\/p>/;
const transactionInfoRegex = /Transaction Info:<\/p>\s*<p>(.*?)<\/p>/;

// Find matches for each field
const dateMatch = html.match(dateRegex);
const amountDebitedMatch = html.match(amountDebitedRegex);
const transactionInfoMatch = html.match(transactionInfoRegex);

// Function to convert date string to YYYY-MM-DD format
const formatDate = (dateString) => {
  const [day, month, year] = dateString.split('-');
  // Handle two-digit year and convert it to four-digit
  const fullYear = parseInt(year) < 50 ? `20${year}` : `19${year}`; // Adjust as needed for your use case
  return `${fullYear}-${month}-${day}`;
};

// Prepare the output object
const output = {
  date: dateMatch ? formatDate(dateMatch[1].trim().split(",")[0]) : null, // Format date
  amountDebited: amountDebitedMatch ? amountDebitedMatch[1].trim() : null, // Trim whitespace
  transactionInfo: transactionInfoMatch ? transactionInfoMatch[1].trim() : null, // Trim whitespace
};

// Return the output as an array of a single object
return { json: output };

return items.map(item => {
  const transactionInfo = item.json.transactionInfo || '';

  // Check if transactionInfo starts with 'UPI'
  if (transactionInfo.startsWith('UPI')) {
    item.json.paymentMode = 'UPI';
  } else {
    item.json.paymentMode = '';
  }

  return item;
});

